package myjavaproject;

public class Progame2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=6;
		int x;
		int y;
		
		for(x=1;x<=n;x++)
		{
			for(y=1;y<=x;y++)
			{
				System.out.print(y);
			}
			System.out.println(" ");
		}
		
		

	}

}
