package myjavaproject;

public class Progame4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=6;
		int x;
		int y;
		
		for(x=1;x<=6;x++)
		{
			for(y=0;y<x;y++)
			{
				System.out.print(x+y);
			}
			System.out.println(" ");
		}

	}

}
 