package myjavaproject;

public class programe3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=6;
		int x;
		int y;
		
		for(x=n;x>=1;x--)
		{
			for(y=1;y<=x;y++)
			{
				System.out.print(y);
			}
			System.out.println(" ");
		}

	}

}
